mapper.select:
SELECT COUNT(*)
  FROM CHAT_ROOM_JOIN
 WHERE CHAT_ROOM_NO = #{chatRoomNo} 
   AND USER_NO = #{userNo}

mapper.delete:
DELETE FROM CHAT_ROOM_JOIN
 WHERE USER_NO = #{userNo} 
 	AND CHAT_ROOM_NO = #{chatRoomNo}

mapper.select:
SELECT COUNT(*)
  FROM CHAT_ROOM_JOIN
 WHERE CHAT_ROOM_NO = #{chatRoomNo}

mapper.update:
UPDATE CHAT_ROOM
   SET STATUS = 'N'
 WHERE CHAT_ROOM_NO = #{chatRoomNo}
